import React,
{
    useState,
    useEffect
} from 'react';
import axios from 'axios';
import './App.css';

const Search = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [allSongs, setAllSongs] = useState([]);
    const [audioSrc, setAudioSrc] = useState('');
    const [audioTitle, setAudioTitle] = useState('');
    const [isPlaying, setIsPlaying] = useState(false);

    useEffect(() => {
        const fetchAllSongs = async () => {
            try {
                const response =
                    await axios.get("http://localhost:5000/api/songs");
                setAllSongs(response.data);
            } catch (error) {
                console.error("Error fetching all songs:", error);
            }
        };
        fetchAllSongs();
    }, []);

    const handleSearch = () => {
        axios.get(`http://localhost:5000/api/songs?title=${searchTerm}`)
            .then(res => {
                setSearchResults(res.data);
            })
            .catch(err => {
                console.error('Error searching for songs:', err);
            });
    };

    const handleSongClick = (song) => () => {
        setAudioSrc(song.audioUrl);
        setAudioTitle(song.title);
        setIsPlaying(true);
    };

    const handleAudioEnded = () => {
        setIsPlaying(false);
    };

    const handlePauseClick = () => {
        setIsPlaying(false);
    };

    return (
        <div className="search-container">
            <h1 className="search-heading">Search</h1>
            <input
                type="text"
                className="search-input"
                value={searchTerm}
                onChange={
                    e =>
                        setSearchTerm(e.target.value)
                } />
            <button className="search-button"
                onClick={handleSearch}>
                Search
            </button>
            <ul className="search-results">
                {searchResults.map(song => (
                    <li key={song._id} className="search-item">
                        {song.title} - {song.artist}
                    </li>
                ))}
            </ul>
            <h1>All Songs</h1>
            {audioSrc && (
                <div>
                    <h2>Now Playing: {audioTitle}</h2>
                    <audio src={audioSrc}
                        autoPlay={isPlaying}
                        onEnded={handleAudioEnded} />
                    {
                        isPlaying ?
                            (<button onClick={handlePauseClick}
                                className='search-button'>Pause</button>) :
                            (<button onClick={
                                () => setIsPlaying(true)
                            }
                                className='search-button'>Play</button>)
                    }
                </div>
            )}
            <div className="all-songs">
                {allSongs.map(song => (
                    <div key={song._id}
                        className="all-song"
                        onClick={handleSongClick(song)}>
                        {song.title} - {song.artist}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Search;
